import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import Sidebar from "../components/Sidebar";
import '../App.css';

class Detail extends Component {
  state = {
    log: {}
  };
  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc
  componentDidMount() {
    API.getLog(this.props.match.params.id)
      .then(res => this.setState({ log: res.data }))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1>
              Ticket #:  {this.state.log.ticket}
              <br></br>
              Hours: {this.state.log.hours}
              <br></br>
              Date: {this.state.log.date}
              </h1>
            </Jumbotron>
          </Col>
        </Row>
        <Row>
          <Col size="md-12">
            <article>
              <h1>Details</h1>
              <hr></hr>
              <p>
                {this.state.log.details}            
              </p>
              
            </article>
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/">Back to Logbook</Link>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Detail;
